// ------------------------------------------------
// strcplen( <str> )
//
// 0: string
// Take a string and eke out its actual length from its CP-coded length

&funpv_strcplen #gfuncs-mortal=
	case( 1, 
		cor( hastoggle( %#, UTF8 ), hastoggle( %#, ACCENTS )), 
		strlen( 
			regeditall( %0, lit(\%<u?\[a-f0-9\]\{3,\}>), - )
		), 

		strlen( %0 )
	)

// ------------------------------------------------
// strcpoffset( <str>, <desired len> )
//
// 0: string
// 1: target length
// Returns the length that you want to put into ljust() and similar length-input

&funpv_strcpoffset #gfuncs-mortal=
    add( %1, 
        sub( 
            strlen( %0 ), 
            strcplen( %0 )
        )
    )

